// Combinar todos los mensajes
const items = $input.all();
let mensajeFinal = '';

for (const item of items) {
  if (item.json.mensaje) {
    mensajeFinal += item.json.mensaje + '\n';
  }
}

mensajeFinal = mensajeFinal.trim() || '⚠️ No se procesaron pedidos';

return [{ json: { mensaje: mensajeFinal } }];
