// Merge State Analyzer + Data Extractor results
const dataExtractor = $json; // Ahora viene directamente de "Call Data Extractor"
const stateAnalyzer = $node['Call State Analyzer'].json;

// Obtener datos de sesi√≥n desde nodos previos disponibles
let sessionData = {};
let sessionId = '';

// Intentar desde diferentes nodos
try {
  const insertUpdate = $node['Insert/Update Session'];
  if (insertUpdate && insertUpdate.json) {
    sessionData = insertUpdate.json;
    sessionId = insertUpdate.json.session_id;
  }
} catch (e) {
  // No existe
}

try {
  const mergeExisting = $node['Merge Existing Session'];
  if (mergeExisting && mergeExisting.json) {
    sessionData = mergeExisting.json;
    sessionId = mergeExisting.json.session_id;
  }
} catch (e) {
  // No existe
}

// Obtener phone
const parsePayload = $node['Parse Payload'].json;
const phone = parsePayload.phone;

console.log('üîÑ MERGING DATA...');
console.log('State Analyzer:', stateAnalyzer);
console.log('Data Extractor:', dataExtractor);
console.log('Session ID:', sessionId);

// Merge state: datos anteriores + datos nuevos de Data Extractor
const mergedState = {
  ...(sessionData.state || {}),
  ...(dataExtractor.captured_data || {}),
  pack: stateAnalyzer.pack_detectado || (sessionData.state?.pack) || null,
  _last_intent: stateAnalyzer.intent,
  _last_mode: stateAnalyzer.new_mode || sessionData.mode,
  _intents_vistos: dataExtractor.captured_data?._intents_vistos || stateAnalyzer.captured_data?._intents_vistos || []
};

// Verificar campos m√≠nimos necesarios
const minimumFields = ['nombre', 'apellido', 'telefono', 'direccion', 'ciudad', 'departamento'];
const camposMinimosCompletos = minimumFields.every(f =>
  mergedState[f] && String(mergedState[f]).trim() !== ''
);

console.log('üìä MERGED STATE:', {
  phone: phone,
  mode: sessionData.mode,
  intent: stateAnalyzer.intent,
  pack: mergedState.pack,
  campos_completos: camposMinimosCompletos,
  order_created: mergedState.order_created || false
});

return {
  phone: phone,
  session_id: sessionId,
  captured_data: mergedState,
  intent: stateAnalyzer.intent,
  pack_detectado: stateAnalyzer.pack_detectado,
  campos_minimos_completos: camposMinimosCompletos,
  order_created: mergedState.order_created || false
};

