// Preservar datos del mensaje despu√©s de insertar sesi√≥n
const sessionFromDB = $json;
const originalData = $node['Create New Session ID'].json;

console.log('üì¶ Preserving message data after session insert');

return {
  session_id: sessionFromDB.session_id,
  phone: sessionFromDB.phone,
  contact_id: sessionFromDB.contact_id || sessionFromDB.callbell_contact_id || '',
  state: sessionFromDB.state || sessionFromDB.captured_data || {},
  tags: sessionFromDB.tags || [],
  mode: sessionFromDB.mode || 'conversacion',
  message: originalData.message  // ‚úÖ Recuperar el objeto message
};

// Formatear snapshot para State Analyzer
const parsePayload = $node['Parse Payload'].json;
const messagesFromDB = $input.all().map(item => item.json);

const phone = parsePayload.phone;
const messages = messagesFromDB.map(m => ({
  id: m.id,
  role: m.role,
  content: m.content,
  direction: m.direction,
  created_at: m.created_at
}));

// Calcular pending: mensajes inbound despu√©s del √∫ltimo outbound
let lastOutboundIdx = -1;
for (let i = messages.length - 1; i >= 0; i--) {
  if (messages[i].direction === 'outbound') {
    lastOutboundIdx = i;
    break;
  }
}

const pending = messages.slice(lastOutboundIdx + 1).filter(m => m.direction === 'inbound');

const state = messagesFromDB[0]?.captured_data || {};
const intents_vistos = state._intents_vistos || [];

console.log('üì∏ Snapshot built:', {
  phone,
  messages_count: messages.length,
  pending_count: pending.length
});

return {
  phone,
  messages,
  pending,
  state,
  intents_vistos
};

// Check if all fields are complete to exit collecting_data mode
const mergedData = $json; // Viene de Merge Data
const capturedData = mergedData.captured_data || {};
const stateAnalyzerIntent = mergedData.intent || 'fallback';

console.log('üîç CHECKING FIELDS COMPLETION...');
console.log('Captured data:', capturedData);

// Campos m√≠nimos necesarios para ofrecer promos (sin correo y barrio)
const minimumFields = ['nombre', 'apellido', 'telefono', 'direccion', 'departamento', 'ciudad'];
const minimumFieldsComplete = minimumFields.every(field => {
  const value = capturedData[field];
  return value && String(value).trim() !== '';
});

// Todos los campos (incluyendo correo y barrio)
const allFields = ['nombre', 'apellido', 'telefono', 'direccion', 'barrio', 'departamento', 'ciudad', 'correo'];
const allFieldsComplete = allFields.every(field => {
  const value = capturedData[field];
  return value && String(value).trim() !== '';
});

console.log('Minimum fields complete:', minimumFieldsComplete);
console.log('All fields complete:', allFieldsComplete);

let finalMode = 'collecting_data';
let finalIntent = stateAnalyzerIntent;
const pack = capturedData.pack || null;

// L√≥gica de cambio de modo e intent
if (allFieldsComplete) {
  // Todos los 8 campos completos ‚Üí cambiar a conversacion
  finalMode = 'conversacion';
  console.log('‚úÖ All 8 fields complete! Switching to conversacion mode');
} else if (minimumFieldsComplete && !pack) {
  // Campos m√≠nimos completos pero no todos, y sin pack ‚Üí ofrecer promos
  finalIntent = 'ofrecer_promos';
  finalMode = 'collecting_data'; // Mantener collecting_data para seguir capturando correo/barrio
  console.log('‚úÖ Minimum fields complete! Changing intent to ofrecer_promos');
} else {
  const missingMinimum = minimumFields.filter(f => !capturedData[f] || String(capturedData[f]).trim() === '');
  console.log('‚è≥ Missing minimum fields:', missingMinimum);
}

// CR√çTICO: Actualizar el intent dentro de captured_data para que se guarde en PostgreSQL
const updatedCapturedData = {
  ...capturedData,
  _last_intent: finalIntent
};

return {
  ...mergedData,
  captured_data: updatedCapturedData,
  final_mode: finalMode,
  intent: finalIntent,
  all_fields_complete: allFieldsComplete,
  minimum_fields_complete: minimumFieldsComplete
};

// Log mensaje bloqueado                                                                                                                                                                                                                    
const payload = $json;                                                                                                                                                                                                                      

let reason = '';                                                                                                                                                                                                                            
if (payload.has_blocked_tag) {                                                                                                                                                                                                              
  reason = `Tags bloqueados: ${payload.tags.join(', ')}`;                                                                                                                                                                                   
} else if (payload.is_old_message) {                                                                                                                                                                                                        
  reason = `Mensaje antiguo: ${payload.message_age_seconds.toFixed(1)}s`;                                                                                                                                                                   
}                                                                                                                                                                                                                                           

console.log('üö´ MESSAGE BLOCKED:', {                                                                                                                                                                                                        
  phone: payload.phone,                                                                                                                                                                                                                     
  reason: reason,                                                                                                                                                                                                                           
  content: payload.content.substring(0, 50)                                                                                                                                                                                                 
});                                                                                                                                                                                                                                         

return {                                                                                                                                                                                                                                    
  blocked: true,                                                                                                                                                                                                                            
  reason: reason,                                                                                                                                                                                                                           
  phone: payload.phone                                                                                                                                                                                                                      
};
