// Validar ciudades con el servidor                                                                                                                                                                                                            
const pedidos = $input.all().map(item => item.json);                                                                                                                                                                                           

const response = await this.helpers.httpRequest({                                                                                                                                                                                              
  method: 'POST',                                                                                                                                                                                                                              
  url: 'http://172.18.0.1:3001/api/validar-pedidos',                                                                                                                                                                                           
  body: { pedidos },                                                                                                                                                                                                                           
  json: true                                                                                                                                                                                                                                   
});                                                                                                                                                                                                                                            

// Retornar pedidos con info de validaci√≥n                                                                                                                                                                                                     
return response.pedidos.map(pedido => {                                                                                                                                                                                                        
  const { _index, ...datos } = pedido;                                                                                                                                                                                                         
  return { json: datos };                                                                                                                                                                                                                      
});

// Parsear respuesta de Claude
const response = $input.first().json;
let pedidos = [];

try {
  const content = response.content?.[0]?.text || response.message?.content || response.content || response.text;
  const jsonMatch = content.match(/\[\s*\{[\s\S]*\}\s*\]/);
  if (jsonMatch) {
    pedidos = JSON.parse(jsonMatch[0]);
  }
} catch (e) {
  console.error('Error parseando respuesta:', e);
}

return pedidos.map(p => ({ json: p }));

// Combinar todos los mensajes
const items = $input.all();
let mensajeFinal = '';

for (const item of items) {
  if (item.json.mensaje) {
    mensajeFinal += item.json.mensaje + '\n';
  }
}

mensajeFinal = mensajeFinal.trim() || '‚ö†Ô∏è No se procesaron pedidos';

return [{ json: { mensaje: mensajeFinal } }];

// Generar mensaje de error con datos completos
const items = $input.all();

if (items.length === 0) {
  return [{ json: { mensaje: '', cantidad: 0 } }];
}

const errores = items.map(item => {
  const p = item.json;
  return `‚Ä¢ *${p.nombres} ${p.apellidos}*
   üìç ${p.direccion}
   üèôÔ∏è ${p.ciudad}
   üì± ${p.celular}
   üí∞ $${p.totalConIva?.toLocaleString() || 'N/A'}
   ‚ùå ${p._error}`;
}).join('\n\n');

return [{
  json: {
    mensaje: `‚ùå *Pedidos rechazados:*\n\n${errores}`,
    cantidad: items.length
  }
}];

// Recopilar resultados del robot con datos completos
const items = $input.all();

if (items.length === 0) {
  return [{ json: { mensaje: '', exitosos: 0, fallidos: 0 } }];
}

const exitosos = items.filter(i => i.json.success);
const fallidos = items.filter(i => !i.json.success);

let mensaje = '';

if (exitosos.length > 0) {
  const detalles = exitosos.map(i => {
    const p = i.json;
    return `‚Ä¢ *#${p.numeroPedido}* - ${p.nombres} ${p.apellidos}
   üìç ${p.direccion}
   üèôÔ∏è ${p.ciudad}
   üì± ${p.celular}
   üí∞ $${p.totalConIva?.toLocaleString() || 'N/A'}`;
  }).join('\n\n');
  mensaje += `‚úÖ *${exitosos.length} pedido(s) creado(s):*\n\n${detalles}`;
}

if (fallidos.length > 0) {
  const errores = fallidos.map(i => {
    const p = i.json;
    return `‚Ä¢ *${p.nombres || p.biginOrderName} ${p.apellidos || ''}*
   üì± ${p.celular || 'N/A'}
   ‚ùå ${p.error}`;
  }).join('\n\n');
  mensaje += `\n\n‚ùå *${fallidos.length} error(es) del robot:*\n\n${errores}`;
}

return [{ json: { mensaje, exitosos: exitosos.length, fallidos: fallidos.length } }];

// Recopilar resultados del robot con datos del pedido original
const items = $input.all();

if (items.length === 0) {
  return [{ json: { mensaje: '', exitosos: 0, fallidos: 0 } }];
}

// Obtener datos originales del nodo anterior (¬øCiudad V√°lida?)
const datosOriginales = $('¬øCiudad V√°lida?').all();

const exitosos = [];
const fallidos = [];

items.forEach((item, index) => {
  const robot = item.json;
  // Buscar datos originales por biginOrderId o por √≠ndice
  const original = datosOriginales.find(d => d.json.biginOrderId === robot.biginOrderId)?.json || datosOriginales[index]?.json || {};

  if (robot.success) {
    exitosos.push({ ...original, ...robot });
  } else {
    fallidos.push({ ...original, ...robot });
  }
});

let mensaje = '';

if (exitosos.length > 0) {
  const detalles = exitosos.map(p => {
    return `‚Ä¢ *#${p.numeroPedido}* - ${p.nombres || 'N/A'} ${p.apellidos || ''}
   üìç ${p.direccion || 'N/A'}
   üèôÔ∏è ${p.ciudad || 'N/A'}
   üì± ${p.celular || 'N/A'}
   üí∞ $${p.totalConIva?.toLocaleString() || 'N/A'}`;
  }).join('\n\n');
  mensaje += `‚úÖ *${exitosos.length} pedido(s) creado(s):*\n\n${detalles}`;
}

if (fallidos.length > 0) {
  const errores = fallidos.map(p => {
    return `‚Ä¢ *${p.nombres || p.biginOrderName || 'N/A'} ${p.apellidos || ''}*
   üì± ${p.celular || 'N/A'}
   ‚ùå ${p.error || 'Error desconocido'}`;
  }).join('\n\n');
  mensaje += `\n\n‚ùå *${fallidos.length} error(es) del robot:*\n\n${errores}`;
}

return [{ json: { mensaje, exitosos: exitosos.length, fallidos: fallidos.length } }];
